package com.geekbrains.lesson6.DZ6;

import org.openqa.selenium.By;
import org.openqa.selenium.WebDriver;
import org.openqa.selenium.WebElement;
import org.openqa.selenium.support.FindBy;
import org.openqa.selenium.support.ui.ExpectedConditions;

public class LoginPage extends BasePage {

    public LoginPage(WebDriver driverCtor) {
        super(driverCtor);
    }

    //input[@id="loginform-email"]
    //input[@id="loginform-password"]
    //button[@class="mm-login"]

    private final String emailLocator = "loginform-email";  //как вариант можно передавать так в visibilityOfElementLocated
    @FindBy(id = emailLocator)
    private WebElement emailField;

    @FindBy(id = "loginform-password")
    private WebElement passwordField;

    @FindBy(className = "mm-login")
    private WebElement buttonLogin;

    @FindBy(xpath = "//img[@src='data:image/png;base64,']")
    private WebElement imgLogo;

    //ниже пишем метод который будет осуществлять 3 действия: 1) ввод логина; 2) ввод пароля; 3) нажатие кнопки "Войти".
    public void login(String login, String password) {
        //Если элементы на странице авторизации не успевают отрисоваться/прогрузиться, то прописываем ожидание
        webDriverWait.until(ExpectedConditions.visibilityOf(emailField)); //суда передаем готовый WebElement
        webDriverWait.until(ExpectedConditions.visibilityOfElementLocated(By.id(emailLocator))); //суда передаем
        // локатор и потом дожидается отрисовки элемента.

        emailField.sendKeys(login);
        passwordField.sendKeys(password);
        buttonLogin.click();
        imgLogo.click();
    }


}
